import React, { Component } from 'react';
import Strapi from 'strapi-sdk-javascript/build/main';
import CarPicker from './CarPicker';
import { convertStrToDate } from './util/DateFormat';

import { getVehicles } from './api';
import NewCalendar from "./DatePicker";
// import { StyleProvider, ThemeProvider } from "vcc-ui";
// import volvo from "vcc-ui/lib/themes/volvo";
//============================================================================//

const strapi = new Strapi("http://localhost:1337");

class App extends Component {
  constructor() {
    super();
    this.state = {
      loadingReservationData: false, // FIXME: toggle ot true
      loadingVehicleData: false,
      vehicleData: [],
      reservationData: [],
      globalError: false,
      availableVehicles: []
    }
  }

  componentDidMount() {
    // getVehicles()
    //   .then(vehicles => {vehicles.json()
    //     .then(vehicleData => {
    //       this.setState({ vehicleData: vehicleData });
    //       return strapi.getEntries("reservations");
    //     })
    //     .then(reservationData => {
    //       this.setState({ reservationData: reservationData, loadingReservationData: false });
    //     })
    //   })
    //   .catch(err => {
    //     console.error(err);
    //     this.setState({globalError: true, loadingReservationData: false });
    //   })
  }

  onSelectCalendarDates = (requestedDates) => {
    // FIXME: should set a range - Morning (before 11:59am), Afternoon (after 12pm), 
    console.log('in parent component dates: ', requestedDates);
    this.setState({ loadingVehicleData: true });
    const availableVehs = [];
    const { reservationData } = this.state;
    for (let i = 0; i < reservationData.length; i++) {
      if (reservationData[i].available) {
        const currRentalStartDate = convertStrToDate(reservationData[i].current_rental_start_date, reservationData[i].current_rental_start_hour);
        const currRentalEndDate = convertStrToDate(reservationData[i].current_rental_end_date, reservationData[i].current_rental_end_hour);
        if (Math.abs(currRentalStartDate - requestedDates[0]) < 300001 ||
          Math.abs(currRentalEndDate - requestedDates[1]) < 300001
        ) { // under 5 min gap difference, acceptable
          break; // FIXME: vehicle not available
        } else {
          availableVehs.push(reservationData[i])
        }
      }
    }
    this.setState({ availableVehicles: availableVehs, loadingVehicleData: false})
  };

  showCarPicker = () => {
    if (this.state.loadingVehicleData) {
      return <div>Loading vehicle data...</div>
    } else if (this.state.availableVehicles.length > 0) {
      return <CarPicker availableCars={this.state.availableVehicles}/>
    } else {
      return null;
      // return <div>No vehicles found for dates you provided :(</div>
    }
  };

  render() {
    console.log('reservationData: ', this.state.reservationData);
    console.log('vehicleData: ', this.state.vehicleData);
    if (this.state.loadingReservationData) {
      return <div>Loading reservation data...</div>
    }
    if (this.state.globalError) {
      return <main>:( Error occurred while fetching information. Please try again later</main>
    } else {
      return (
        <main className='content-container'>
          <NewCalendar />
          {/*<CalendarComponent onSelectCalendarDates={this.onSelectCalendarDates}/>*/}
          {this.showCarPicker()}
        </main>
      );
    }
  }
}

export default App;
