import React, { Component } from 'react'
import moment from "moment"
import { Calendar, momentLocalizer } from "react-big-calendar";

import 'react-big-calendar/lib/sass/styles.scss'
import './styles.scss'
import './prism.scss'

import { myEventsList } from "./newEvents";
//============================================================================//


class NewCalendar extends Component {
  constructor(...args) {
    super(...args);
    this.state = {
      reservation: {
        start: {
          date: null,
          timeOfDay: ""
        },
        end: {
          date: null,
          timeOfDay: ""
        }
      },
      startDate: null,
      endDate: null,
      tooManyDaysWarning: false
    }
  }

  onSelectSlots = (e) => {
    if (e.slots.length > 4) {
      return this.setState({ tooManyDaysWarning: true })
    } else {
      return this.setState({
        reservation: {
          start: {
            ...this.state.reservation.start,
            date: e.slots[0],
          },
          end: {
            ...this.state.reservation.end,
            date: e.slots[e.slots.length - 1]
          }
        },
        startDate: JSON.stringify(e.slots[0]),
        endDate: JSON.stringify(e.slots[e.slots.length - 1]),
        tooManyDaysWarning: false
      })
    }
  };

  onClickSubmit = () => {
    this.props.onSelectCalendarDates(this.state.reservation);
  };

  render() {
    const localize = momentLocalizer(moment);
    return (
      <>
        <div className="examples">
          <div className="example">
            <Calendar
              selectable={true}
              onSelectSlot={(e) => this.onSelectSlots(e)}
              views={['month']}
              localizer={localize}
              events={myEventsList}
              startAccessor="start"
              endAccessor="end"
            />
            <h4>Tip: hold mouse down and drag across multiple cells</h4>
            <ul>
              <li>
                Selected start date: {this.state.startDate}
                <div>
                  <input
                    type="checkbox"
                     name="morning"
                     value="morning"
                    checked={this.state.reservation.start.timeOfDay === "morning"}
                  />
                    <label htmlFor="morning">Morning</label>
                </div>
              </li>
              <li>Selected end date: {this.state.endDate}</li>
            </ul>
            <span style={{color: "orange"}}>
            {this.state.tooManyDaysWarning ?
              "You Selected more than the limited four consecutive (4) days. Must be exception case. Proceed w/ caution"
              : ""}
            </span>
            <button onClick={() => this.onClickSubmit()}>Submit reservation</button>
          </div>
        </div>
      </>
    );
  }
}

export default NewCalendar;
