import React from "react";
import { Icon } from ".";
import createSnapshot from "../../test/create-snapshot";
describe("<Icon /> snapshot tests", function () {
  it("renders correctly with type=globe", function () {
    var snapshot = createSnapshot(React.createElement(Icon, {
      type: "globe"
    }));
    expect(snapshot).toMatchSnapshot();
  });
  it("renders correctly with custom title", function () {
    var snapshot = createSnapshot(React.createElement(Icon, {
      type: "globe",
      title: "custom title"
    }));
    expect(snapshot).toMatchSnapshot();
  });
});