import React from "react";
import { storiesOf } from "@storybook/react";
import { withDarkBackground } from "../../../stories/decorators";
import { Link } from "./";
storiesOf("Actions/Link", module).addWithJSX("With Arrow", function () {
  return React.createElement(Link, {
    href: "http://www.volvocars.com/",
    arrow: "right"
  }, "This is a link component");
});
storiesOf("Actions/Link", module).addDecorator(withDarkBackground).addWithJSX("On Dark With Arrow", function () {
  return React.createElement(Link, {
    href: "http://www.volvocars.com/",
    intent: "primary-light",
    arrow: "right"
  }, "This is a link component");
});